function g = NewStimScriptList% Part of the NewStim package%%  LIST = NEWSTIMSCRIPTLIST%%  Returns a list of strings containing the names of stimscript objects.%%  Questions to vanhoosr@brandeis.edustimdir = fileparts(which('NewStimScriptList'));g = {};d = dir(stimdir);for i=1:length(d)    if d(i).isdir == 1 &  d(i).name(1) == '@' % needed one & for MACOS9        g{end+1} = d(i).name(2:end);    endend