function adjust_duration(aqfilename, stimScript, newfilename)aqInfo = loadStructArray(aqfilename);T = duration(stimScript);numReps = fix(T/10) + 2;  % do an extra two just in casefor i=1:length(aqInfo), aqInfo(i).reps = numReps; end;writeAcqStruct(newfilename,aqInfo); %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%function writeAcqStruct(fname, inDat)[fid,msg] = fopen(fname,'wt');if fid==-1  disp(['ADJUST_DURATION:WRITEACQSTRUCT:failed to open ' fname ' -- ' msg]);  return;end;fn = fieldnames(inDat(1));s = '';for i=1:length(fn),        s = [ s char(9) fn{i}];end;s = s(2:end);fprintf(fid,'%s\n',s);for i=1:length(inDat),        t = char(9);        s=[inDat(i).name t inDat(i).type t inDat(i).fname t ...                num2str(inDat(i).samp_dt,15) t int2str(inDat(i).reps) t ...                int2str(inDat(i).ref) t int2str(inDat(i).ECGain)];        fprintf(fid,'%s\n',s);end;fclose(fid);% wait two seconds to give data acquisition time to parse the file