function NewStimCalibrate% NewStimCalibrate.m%%  A user-specific configuration file which contains calibration code%  for a specific machine and monitor.  Read each line of code and its comments%  in order to set each parameter.%disp('NEWSTIMCALIBRATE: Function is deprecated, and will be removed in the future.');NewStimGlobals;if haspsychtbox,    if strcmp(computer,'MAC2')        % set clock multiplier...see help GetSecsTest for info        % check timing of Damian's rig on 2007-01-24 and corrected delay of TTL        % perfect over 5mins within 1 frame. MAC-monitorsetting at 75 Hz        % Screen('Preference','SecondsMultiplier',1.000230644770116);        Screen('Preference','SecondsMultiplier',0.999995);        Screen('Preference','Backgrounding',0);    endelse    StimWindowGlobals;    StimWindowRect = [ 0 0 640 480 ];end;switch host    case 'mac' % client electrophys seyup        setserials;        % NewStimPixelsPerCm = 17.49;  % from 2002-08-01 to 2003-01-27        % NewStimPixelsPerCm = 7.6190; % from 2004-02-19 beamer with mac 640x480-        % NewStimPixelsPerCm = 9.0141 ; % from 2006-02-06 beamer with mac 640x480-		NewStimPixelsPerCm = mean([640/80 480/61]) ; % from 2010-01-14 beamer with mac 640x480-        NewStimRemoteCommDir = 'data:stims:';        % NewStimViewingDistance = 56; % introduced on 2010-01-11        NewStimViewingDistance = 15; % from 2010-01-14 beamer    case 'nori001'  % host electrophys setup        setserials;        NewStimRemoteComputerType = 'MAC2';        NewStimRemoteCommDir = 'C:\Data\stims\';        % NewStimPixelsPerCm should not be used on host        % NewStimPixelsPerCm = 18.0832; % from 2003-01-27 -    case 'nin108' % client twophoton setup        setserials;        NewStimRemoteComputerType = 'PCWIN';        NewStimPixelsPerCm = 1600/47.5; % on 13-8-2008        NewStimRemoteCommDir = '\\OLYMPUS-0603301\data\stims\';        NewStimViewingDistance = 57;         warning('NEWSTIMCALIBRATE: check viewing distance');    case 'olympus-0603301'        setserials;        %NewStimRemoteComputerType = 'PCWIN';        %warning('NEWSTIMCALIBRATE: Testing Linux stimulus display!');        NewStimRemoteComputerType = 'GLNX86';  %testing 2012-02-24        NewStimRemoteCommDir = 'D:\Data\stims\';    case 'nin137' % client desktop josephine        setserials;        NewStimRemoteComputerType = 'PCWIN';        NewStimPixelsPerCm = 1280/30; % incorrect, just for testing        NewStimRemoteCommDir = '\\giskard.intern.nin.knaw.nl\data\stims';        NewStimViewingDistance = 57; % incorrect, just for testing    case '3dstim' % 3d-stimulus computer        % for old newstim        setserials;        NewStimRemoteComputerType = 'PCWIN';        NewStimPixelsPerCm = 1680 / 47.3; % Zalman 3d monitor 2010-05-28        NewStimRemoteCommDir = '\\OLYMPUS-0603301\data\stims\';        NewStimViewingDistance = 53; %       %  NewStimViewingDistance = 15; %                warning('NEWSTIMCALIBRATE: viewingdistance is temporarily set to short distance');    otherwise        % do nothing        %setserials;        %disp('NEWSTIMCALIBRATE: Unknown host. Assuming new host.');        %switch computer        %    case {'GLNX86','LNX86'}        %        NewStimRemoteCommDir = '/home/data/local/stims';        %    case 'PCWIN'        %        NewStimRemoteCommDir = 'C:\Data\stims\';        %end        %NewStimRemoteComputerType = 'PCWIN';        %NewStimViewingDistance = 57; % incorrect, just for testing        %NewStimPixelsPerCm = 18.0832; % from 2003-01-27 -        % NewStimPixelsPerCm should not be used on hostendfunction setserialsStimSerialGlobals; % see help file for descriptionif strcmp(computer,'MAC2')    StimSerialScriptIn = '.Bin';    StimSerialScriptInPin = 'dtr';    StimSerialScriptOut = '.Bout';    StimSerialScriptOutPin = 'dtr';			    StimSerialStimIn = '.keyU2X02in';    StimSerialStimInPin = 'dtr';    StimSerialStimOut = '.keyU2X02out';    StimSerialStimOutPin = 'dtr';			StimSerialStimIn = '.Bin';    StimSerialStimInPin = 'dtr';    StimSerialStimOut = '.Bout';    StimSerialStimOutPin = 'dtr';		else    StimSerialScriptIn = 'COM1';    StimSerialScriptInPin = 'dtr';    StimSerialScriptOut = '.Bout';    StimSerialScriptOutPin = 'dtr';    StimSerialStimIn = 'COM1';    StimSerialStimInPin = 'rts';    StimSerialStimOut = '.Bout';    StimSerialStimOutPin = 'rts';end