function NelsonLabToolsInitLocal % inits local pathname variables for NelsonLabToolsglobal sharedirNLT initdatapathNLT initanalysispathNLT initremotedirpathNLT;global initextractpathNLT initacqreadyNLT;remotecommglobalsswitch computer  case {'PCWIN','PCWIN64'}     datadir = get_datadir;  case {'LNX86','GLNX86','GLNXA64'}     datadir='/home/data/';  case {'MACI64'}     datadir='/tmp/';  otherwise    logmsg(['Do not know what to do for computer type ' computer ]);   datadir='';end sharedirNLT = Remote_Comm_dir; %fullfile(datadir,'stims',filesep); initdatapathNLT = fullfile(datadir,filesep);initanalysispathNLT = datadir;initremotedirpathNLT = sharedirNLT;initextractpathNLT = fullfile(datadir,filesep);initacqreadyNLT= fullfile(datadir,filesep,'acqReady'); function datadir = get_datadirdatadir = fullfile('C:','Data','InVivo','Electrophys',capitalize(host));if exist(datadir,'dir')    returnenddatadir = fullfile('D:','Data','InVivo','Electrophys',capitalize(host));if exist(datadir,'dir')    returnenddatadir = fullfile('C:','Data');if exist(datadir,'dir')    returnenddatadir = fullfile('D:','Data');if exist(datadir,'dir')    returnelse    logmsg('No Data folder found.');end