StimSerialGlobals;if isempty(StimSerialScript),	StimSerialScript=StimSerial('Open',StimSerialScriptIn,StimSerialScriptOut,9600);end;if isempty(StimSerialStim),	if strcmp(StimSerialScriptIn,StimSerialStimIn)&...		strcmp(StimSerialScriptOut,StimSerialStimOut),			StimSerialStim = StimSerialScript;	else,		StimSerialStim=StimSerial('Open',StimSerialStimIn,StimSerialStimOut,9600);	end;end;